mapper.select:
SELECT COUNT(1) CNT FROM ( SELECT B.no ,B.input_dt
FROM board B WHERE B.no>0
) B

mapper.insert:

SELECT COALESCE(MAX(no),0)+1 AS no FROM board

insert board(
no
,id
,title
,content
,input_dt
) values(
#{no}
,#{id,jdbcType=VARCHAR}
,#{title,jdbcType=VARCHAR}
,#{content,jdbcType=VARCHAR}
,NOW()
)

mapper.update:
   	  INSERT INTO board(
	   	  no
			,id
			,TITLE
			,content
			,updt_dt
		) VALUES(
			#{no, jdbcType=VARCHAR}
			, #{id, jdbcType=VARCHAR}
			, #{title, jdbcType=VARCHAR}
			, #{content, jdbcType=VARCHAR}
			, NOW()
		)
		ON DUPLICATE KEY UPDATE
		no = #{no, jdbcType=VARCHAR}
		, id = #{id, jdbcType=VARCHAR}
		, TITLE = #{title, jdbcType=VARCHAR}
		, content = #{content, jdbcType=VARCHAR}
		, updt_dt = NOW()
